mapper.update:
UPDATE PHOTOTABLE
	SET pTitle = #{pTtitle}
	, pFile = #{pFile}
	, pHashTag = #{pHashTag}
	WHERE pidx = #{pidx}

mapper.insert:

	SELECT MAX(pidx)
		FROM phototable


insert into imagetable(
	pidx, fileName, uploadPath, uuid
	) values (
	#{pidx}, #{fileName}, #{uploadPath}, #{uuid}
	)
