mapper.select:
select *
FROM phototable

order by pidx desc LIMIT #{rowStart}, #{rowEnd}

mapper.insert:
 	
		
		INSERT INTO phototable(
			 pTitle
			, pDate
			, uidx
			, pHashTag
			, pHit
			, pWriter
		) VALUES(
			 #{pTitle}
			, NOW()
			, #{uidx}
			, #{pHashTag}
			, #{pHit}
			, #{pWriter}
		)

insert into imagetable(
	pidx, fileName, uploadPath, uuid
	) values (
	#{pidx}, #{fileName}, #{uploadPath}, #{uuid}
	)

mapper.update:
UPDATE phototable SET

	pTitle = #{pTitle}
,	pHashTag = #{pHashTag}
,	pDate = NOW()

WHERE pidx = #{pidx}

		UPDATE imagetable SET
		
			fileName = #{fileName}
		,	uploadPath = #{uploadPath}
		,	uuid = #{uuid}

		WHERE pidx = #{pidx}
