mapper.select:
SELECT * FROM (
SELECT ROWNUM AS rnum, A.* FROM (
SELECT * FROM boardtable

ORDER BY bidx DESC
)A
) WHERE rnum BETWEEN #{rowStart} AND #{rowEnd}

mapper.update:
UPDATE boardtable
SET
title
= #{title}
, content = #{content}
, hashTag = #{hashTag}
WHERE bidx
= #{bidx}

mapper.insert:
INSERT INTO replytable(
ridx
, rContent
, redate
, writer
, bidx
, rdelyn
, uidx
)VALUES(
pridx_seq.NEXTVAL
, #{rContent}
, sysdate
, #{writer}
, #{bidx}
, 'N'
, #{uidx}
)

mapper.update:
UPDATE replytable
SET 
rContent = #{rContent}
WHERE ridx = #{ridx}

update replytable
SET
rdelyn = 'Y' WHERE ridx = #{ridx}
