mapper.select:
SELECT * FROM (
SELECT ROWNUM AS rnum, A.* FROM (
SELECT * FROM boardtable

ORDER BY bidx DESC
)A
) WHERE rnum BETWEEN #{rowStart} AND #{rowEnd}

mapper.update:
UPDATE boardtable
SET
title
= #{title}
, content = #{content}
, hashTag = #{hashTag}
WHERE bidx
= #{bidx}

UPDATE replytable
SET 
rContent = #{rContent}
WHERE ridx = #{ridx}

update replytable
SET
rdelyn = 'Y' WHERE ridx = #{ridx}
