mapper.update:
UPDATE replytable
SET 
rContent = #{rContent}
WHERE ridx = #{ridx}

update replytable
SET
rdelyn = 'Y' WHERE ridx = #{ridx}

update boardtable b
set b.reply_count = (select count(reply_count) from replytable where bidx = #{bidx})
where b.bidx = #{bidx}

UPDATE liketable
set like_check = like_check + 1
where uidx=#{uidx} and replytable = #{replytable}

UPDATE liketable
SET like_check = 0
WHERE uidx= #{uidx}
AND ridx = #{ridx}
