mapper.select:
SELECT COUNT(*)
FROM usertable
WHERE id = #{id}

mapper.insert:
INSERT INTO usertable(
	uidx
, id
, password
, name
, phone
, birth
, gender
, rdate
)VALUES(
	puidx_seq.nextval
	, #{id}
	, #{password}
	, #{name}
	, #{phone}
	, #{birth}
	, #{gender}
	, sysdate
)

mapper.select:
SELECT uidx
, id
, name
FROM usertable
WHERE id = #{id}
and password = #{password}
